const detailPending = ref(false);

async function getEvents(b) {
  try {
    detailPending.value = true;
    e.value = null;

    const res = await axios.post(`${url.url}/api/events`, b);

    e.value = res.data;
    detailPending.value = false;
  } catch (error) {
    console.error("Error fetching events:", error);
  }
}

