const isKeno = ref(false);

const betSlipICon = ref("PlatinumHounds");
const selectedGame = ref("PlatinumHounds");

async function handleGameChange(game, isFinshed) {
  selectedGame.value = betSlipICon.value;
  betSlipICon.value = game.searchName;

  if (isFinshed) {
    e.value = null;
    // selectedBets.value = [];
    betPlacedSuccess.value = false;
    isKeno.value = false;
    eventDetail.value = {};
    await getEvents({
      name: game.searchName,
      feedId: game.feedId,
    });
  } else {
    if (selectedGame.value !== game.searchName) {
      e.value = null;
      selectedBets.value = [];
      betPlacedSuccess.value = false;
      isKeno.value = false;
      eventDetail.value = {};
      await getEvents({
        name: game.searchName,
        feedId: game.feedId,
      });
    }
  }
  if (game.searchName === "PlatinumHounds") {
    i = "GreyhoundJackets";
  } else if (
    game.searchName === "DashingDerby" ||
    game.searchName === "HarnessRacing"
  ) {
    i = "HorseSilks";
  } else if (game.searchName === "SpeedSkating") {
    i = "SpeedSkating";
  } else if (game.searchName === "SteepleChase") {
    i = "HorseSilks";
  } else if (
    game.searchName === "MotorRacing" ||
    game.searchName === "SingleSeaterMotorRacing"
  ) {
    i = "MaxCarHelmets";
  } else if (game.searchName === "CycleRacing") {
    i = "CyclistHelmets";
  }
  const { images } = useFolderImages(i);
  imageDir.value = images.value;
}

