const handleWinClicked = (bet) => {
  // selectedBets.value.push(bet);
  handleComboClicked(bet.number, bet.id, true);
  betPlacedSuccess.value = false;

  const index = selectedBets.value.findIndex(
    (b) =>
      b.id === bet.id && b.betType === bet.betType && b.number === bet.number
  );

  if (index !== -1) {
    // Already exists → remove it
    selectedBets.value.splice(index, 1);
  } else {
    // Doesn't exist → add it
    selectedBets.value.push(bet);
  }
  trackingSelectedNumbers.value.push(bet.number);
};

const handleComboClicked = (n, id, from) => {
  if (!comboSelections.value[id]) {
    comboSelections.value[id] = [];
  }
  const index = comboSelections.value[id].indexOf(n);

  if (index === -1) {
    comboSelections.value[id].push(n);
  } else {
    if (!from) {
      comboSelections.value[id].splice(index, 1);
    }
  }
};

const handleClearCombo = (id) => {
  comboSelections.value[id] = [];
};

function comboHelper(type, eventId) {
  const arr = comboSelections.value[eventId];
  if (
    type === "ReverseForecast" ||
    type === "Swinger" ||
    type === "ReverseTricast"
  ) {
    return [arr.join(","), "", ""];
  } else if (type === "Forecast") {
    const arr = comboSelections.value[eventId];
    if (arr.length === 2) {
      return arr.map(String).concat("");
    } else if (arr.length === 3) {
      const result = [];
      for (let i = 0; i < arr.length - 1; i++) {
        result.push(`${arr[i]},${arr[i + 1]}`);
      }
      result.push("");
      return result;
    } else if (arr.length >= 4) {
      const str = arr.join(",");
      return [str, str, ""];
    }
  } else if (type === "Tricast") {
    if (arr.length === 3) {
      const result = [];
      result.push(String(arr[0]));
      result.push(arr.slice(1).join(","));
      result.push(String(arr[arr.length - 1]));
      return result;
    } else if (arr.length >= 4) {
      let times = 3;
      const str = arr.join(",");
      return Array(times).fill(str);
    }
  }
}

const handleCombos = async (
  type,
  eventId,
  Id,
  typeToSend,
  marketValue,
  eventValue,
  displayDesc,
  gameName,
  gameId
) => {
  betPlacedSuccess.value = false;

  detailPending.value = true;
  const selection = comboHelper(typeToSend, eventId);
  const res = await axios.post(`${url.url}/api/combo`, [
    {
      ID: 3,
      FeedEventId: Id,
      EventTypeValue: eventValue,
      MarketClassValue: marketValue,
      MarketClassName: "type",
      SelectionIds: selection,
    },
  ]);
  const data = ref(res.data);

  const slip = {
    id: eventId,
    betType: displayDesc,
    name: data.value.data[0].MaxNotation,
    odd: data.value.data[0].MaxOdds,
    date: "2025/09/02 11:56:00",
    gameId,
    longId: Id,
    stake: 10,
    number: 1,
    stakeUpdated: false,
    isCombo: true,
    data: data.value.data[0],
    gameName,
  };

  console.log(slip);

  const exists = selectedBets.value.some(
    (b) => b.betType === slip.betType && b.name === slip.name
  );

  if (!exists) {
    selectedBets.value.push(slip);
  }

  // selectedBets.value.push(slip);

  detailPending.value = false;
};

