async function handlePlaceBet() {
  placingBet.value = true;
  const res = await axios.post(
    `${url.url}/api/placeBet`,
    {
      selectedBets: selectedBets.value,
      username: auth?.user?.user,
      cashier: auth?.user?.cashier,
    },
    {
      withCredentials: true,
    }
  );

  if (res.data.expired) {
    router.push({
      path: "/RetailUser/Login",
      query: { msg: res.data.message },
    });
    return;
  }

  if (res.data.limited) {
    selectedBets.value = [];
    placingBet.value = false;
    limitReached.value = true;
    return;
  }

  if (res.data.timeExpired) {
    expiredBets.value = selectedBets.value;
    selectedBets.value = [];
    placingBet.value = false;
    router.push("/");
    return;
  }

  if (res.data.success) {
    placingBet.value = false;
    selectedBets.value = [];
    betPlacedSuccess.value = true;
    if (res.data.totalAmount / res.data.limita > 0.9) {
      limitReached.value = true;
    }

    await printBet(res.data.data);
    // if (!printer.online) {
    // await checkPrinterIsOnline();
    // return;
    // }
  }
}

async function printBet(data) {
  try {
    const response = await fetch("http://localhost:8080/PRINT", {
      method: "POST",
      headers: {
        "Content-Type": "text/plain",
      },
      body: JSON.stringify(data),
    });
  } catch (error) {
    console.error("Error printing:", error);
    // await checkPrinterIsOnline();
  }
}

async function handleCancelTicket(id) {
  cancelTicketLoader.value = true;
  cancelTickets.value = [];
  cancleTicketMessage.value = null;
  ticketCancelledSuccess.value = false;
  try {
    const res = await axios.post(`${url.url}/api/cancel`, {
      ticketId: id,
      username: auth?.user?.user,
      cashier: auth?.user?.cashier,
    });

    if (res.data.success) {
      cancelTickets.value = res.data.data;
      cancelTicketId.value = id;
    } else {
      cancleTicketMessage.value = res.data.message;
    }
    cancelTicketLoader.value = false;
  } catch (error) {
    console.log(error);
  }
}

async function proceedCancelTicket() {
  try {
    cancelTicketLoader.value = true;

    const response = await axios.post(`${url.url}/api/cancelBet`, {
      ticketID: cancelTicketId.value,
      username: auth?.user?.user,
      cashier: auth?.user?.cashier,
    });

    if (response.data.success) {
      cancelTicketLoader.value = false;
      ticketCancelledSuccess.value = true;
      printBet(response.data.cancelTicket);
    } else {
      cancelTicketLoader.value = false;
      cannotCancelTicket.value = true;
    }
  } catch (error) {
    console.log(error);
  }
}

async function handleRedeemTicket(id) {
  redeemTicketLoader.value = true;
  redeemTicket.value = [];
  redeemTicketMessage.value = null;
  ticketRedeemedSuccess.value = false;
  try {
    const res = await axios.post(`${url.url}/api/redeem`, {
      ticketId: id,
      username: auth?.user?.user,
      cashier: auth?.user?.cashier,
    });

    if (res.data.success) {
      // redeemTicket.value.rows = res.data.rows;
      // redeemTicket.value.numbers = res.data.numbers;
      redeemTicket.value = res.data.data;
      redeemTicketId.value = id;
    } else {
      redeemTicketMessage.value = res.data.message;
      redeemTicket.value = { rows: [], numbers: [] };
    }
    redeemTicketLoader.value = false;
  } catch (error) {
    console.log(error);
  }
}

async function proceedRedeemTicket(winners) {
  try {
    redeemTicketLoader.value = true;

    const response = await axios.post(`${url.url}/api/redeemBet`, {
      ticketID: redeemTicketId.value,
      winners,
      username: auth?.user?.user,
      cashier: auth?.user?.cashier,
    });

    if (response.data.success) {
      redeemTicketLoader.value = false;
      ticketRedeemedSuccess.value = true;

      redeemTicket.value = { rows: [], numbers: [], placePaysOn: null };
      printBet(response.data.ticket);
    }
  } catch (error) {
    console.log(error);
  }
}

async function checkPrinterIsOnline() {
  try {
    printer.changePrinterState(false);
    const res = await fetch("http://localhost:8000/ISONLINE");
    showPrinterCheckModal.value = false;
    printer.changePrinterState(true);
  } catch (error) {
    showPrinterCheckModal.value = true;
    printer.changePrinterState(false);
  }
}

async function getBalance(username) {
  const res = await axios.post(`${url.url}/api/getBalance`, {
    username: auth?.user?.user,
    cashier: auth?.user?.cashier,
  });
  balance.value = res.data.totalAmount;
}

async function getBalanceData(username) {
  balanceDataLoader.value = true;
